package DreamJava;

public class Array5 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
      int a[][]= {{1,2},{3,4},{6,7}};
      for (int i = 0; i < 3; i++) 
		{

			for (int j = 0; j < 2; j++) 
			{
				System.out.print(a[i][j]+" ");
			}
			System.out.println();
		}
	}

}
